    private void prokelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_prokelActionPerformed
        // TODO add your handling code here:
        String teksdiago1 = indiago1.getText();
        double inputdiago1 = Double.parseDouble(teksdiago1);
        String teksdiago2 = indiago2.getText();
        double inputdiago2 = Double.parseDouble(teksdiago2);
        double sisi1 = inputdiago1 / 2; 
        double sisi2 = inputdiago2 / 2;
        double hasilsimir = (Math.pow(sisi1,2)) + (Math.pow(sisi2,2));//rumus phytagoras
               hasilsimir=Math.sqrt(hasilsimir); //rumus phytagoras
        int miring = (int)hasilsimir;
        double hasilkel = 4 * miring;
        String outputsisi = String.valueOf(miring);
        outsisi.setText(outputsisi);
        String outputkel = String.valueOf(hasilkel);
        outkel.setText(outputkel);
    }//GEN-LAST:event_prokelActionPerformed

