    private void proluasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_proluasActionPerformed
        // TODO add your handling code here:
        String teksdiago1 = indiago1.getText();
        double inputdiago1 = Double.parseDouble(teksdiago1);
        String teksdiago2 = indiago2.getText();
        double inputdiago2 = Double.parseDouble(teksdiago2);
        double hasilluas = inputdiago1 * inputdiago2 / 2;
        String outputluas = String.valueOf(hasilluas);
        outluas.setText(outputluas);
    }//GEN-LAST:event_proluasActionPerformed

